import { makeAutoObservable, when } from "mobx"

export default class AnimalStore {
    name
    energyLevel
    constructor(name: string) {
        this.name = name
        this.energyLevel = 100
        makeAutoObservable(this)
        when(
            () => this.isHungry,
            () => this.a
        )
    }
    
    reduceEnergy() {
        this.energyLevel -= 10
    }

    get isHungry() {
        return this.energyLevel < 10
    }
    get a() {
        return this.energyLevel < 100;
    }
}

